Ext.onReady(function(){var a=new Ext.FormPanel({id:"pick-shipment-form",title:"Pack Shipment",renderTo:"pack-shipment",url:"connect.php?moduleId=qo-shipments&action=packShipment",autoScroll:true,items:[{xtype:"textfield",fieldLabel:"Shipment Id",name:"id",listeners:{specialkey:function(b,c){if(c.getKey()==13){Ext.getCmp("pick-shipment-form").form.submit({method:"POST",waitTitle:"Connecting",waitMsg:"Sending data...",success:function(d,e){var f=Ext.util.JSON.decode(e.response.responseText);document.getElementById("message").innerHTML=f.info;Ext.getCmp("pick-shipment-form").form.findField("id").focus(true);},failure:function(d,e){var f=Ext.util.JSON.decode(e.response.responseText);document.getElementById("message").innerHTML=f.errors.reason;Ext.getCmp("pick-shipment-form").form.findField("id").focus(true);}});}}}},{layout:"column",items:[{columnWidth:0.6,layout:"form",border:false,items:[{id:"postalReferenceNo",xtype:"textfield",fieldLabel:"Postal Referece",name:"postalReferenceNo"}]},{columnWidth:0.4,layout:"form",border:false,items:[{id:"button",xtype:"button",name:"textvalue",text:"Disable",handler:function(){if(Ext.getCmp("postalReferenceNo").disabled==true){Ext.getCmp("postalReferenceNo").setDisabled(false);Ext.getCmp("button").setText("Disable");}else{Ext.getCmp("postalReferenceNo").setDisabled(true);Ext.getCmp("button").setText("Enable");}}}]}]}],buttons:[{text:"Submit",handler:function(){Ext.getCmp("pick-shipment-form").form.submit({method:"POST",waitTitle:"Connecting",waitMsg:"Sending data...",success:function(b,c){var d=Ext.util.JSON.decode(c.response.responseText);document.getElementById("message").innerHTML=d.info;},failure:function(b,c){var d=Ext.util.JSON.decode(c.response.responseText);document.getElementById("message").innerHTML=d.errors.reason;}});}}]});});